mapper.select:
select * from Task where user_id = #{userId}

    and title like '%' || #{searchKeyword} || '%'


   and  to_char(create_date,'yyyy-MM-dd') = #{searchKeyword}

mapper.update:
UPDATE Task
SET title = #{title}, update_Date =current_timestamp, content = #{content}
WHERE task_seq = #{taskSeq}

UPDATE Task
SET status = #{status}
WHERE task_seq = #{taskSeq}
