mapper.select:
select * from Task where user_id = #{userId}

    and title like '%' || #{searchKeyword} || '%'


   and  to_char(create_date,'yyyy-MM-dd') = #{searchKeyword}


    and  to_char(update_date,'yyyy-MM-dd') = #{searchKeyword}

mapper.update:
UPDATE Task
SET title = #{title}, update_Date =current_timestamp, content = #{content}
WHERE task_seq = #{taskSeq}

mapper.select:
SELECT t.*, c.group_Name FROM task t JOIN member m ON t.user_id = m.user_id JOIN class c ON m.group_seq = c.group_seq WHERE m.group_seq = #{groupSeq}
and title like '%' || #{searchKeyword} || '%'
and user_id like '%' || #{searchKeyword} || '%'
and to_char(create_date,'yyyy-MM-dd') = #{searchKeyword}

mapper.update:
UPDATE Task
SET status = #{status}
WHERE task_seq = #{taskSeq}
